    private void cmbActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cmbActionPerformed
        // TODO add your handling code here:
        if (iny1.getText().equals("") || iny2.getText().equals("") || inx1.getText().equals("")) {
            JOptionPane.showMessageDialog(null, "isi semua data");
            getToolkit().beep();
        } else {
            String teksiny1 = iny1.getText();
            double inputy1 = Double.parseDouble(teksiny1);
            String teksiny2 = iny2.getText();
            double inputy2 = Double.parseDouble(teksiny2);
            String teksinx1 = inx1.getText();
            double inputx1 = Double.parseDouble(teksinx1);
            DecimalFormat df = new DecimalFormat("#.##");

            if (cmb.getSelectedIndex() == 0) {
                iny1.setText(null);
                iny2.setText(null);
                inx1.setText(null);
                outx2.setText(null);
                rumus.setText("Hasilnya : ");
            }
            if (cmb.getSelectedIndex() == 1) {
                rumus.setText("Hasilnya : Y1/Y2 = X1/X2");
                double hasil = inputx1 * inputy2 / inputy1;
                String formathasil = df.format(hasil);
                outx2.setText(formathasil);
            }
            if (cmb.getSelectedIndex() == 2) {
                rumus.setText("Hasilnya : Y1/Y2 = X2/X1");
                double hasil = inputx1 * inputy1 / inputy2;
                String formathasil = df.format(hasil);
                outx2.setText(formathasil);
            }
        }
    }//GEN-LAST:event_cmbActionPerformed

